package chapter7RandomValues;

public class User {

	private String name;
	private String surname;
	
	public User(String name, String surname) {
		this.name  = name;
		this.surname = surname;

	}
	
	public String getName() {
		// TODO Auto-generated method stub
		return this.name;
	}

	public String getSurname() {
		// TODO Auto-generated method stub
		return this.surname;
	}

}
